INSERT INTO `yiliao_jigou` (`j_id`, `j_name`, `j_shenfen`, `j_jiage`, `j_jibie`, `j_time`, `j_ip`, `j_content`, `j_zhuanjia`, `user_name`, `j_image_count`, `j_click`, `j_star`, `home_page`, `google_map`, `j_weizi`, `click`) VALUES
(35, '上海仁济医院PET-CT中心', '上海', '7500', '三甲', '2009-08-11 10:03:56', '116.228.54.246', '【医院介绍】仁济医院建于1844年,迄今已有164年的历史,成为学科门类齐全，集医疗、教学、科研于一体的综合性三级甲等医院。\r\n\r\n【中心介绍】2003年7月伴随着上海第一台PET-CT,GE''s Discovery LS PET/CT 装置在上海仁济医院顺利装机、投入试运行，上海仁济医院PET-CT中心宣告成立。PET-CT运行、操作、诊疗的质量保证与质量控制的规范和流程，并在随后的实际操作中不断改进、充实，目前已经形成了一个比较稳定、完善的PET-CT中心运行、诊疗和管理体系。\r\n\r\n【设备型号】GE Discovery LS PET/CT\r\n\r\n\r\n\r\n【乘车路线】01、581、593、606、610、614、771、772、789、\r\n814、980、989、大桥二线、隧道九线、轨道交通四号线、六号线均可到达。\r\n\r\n\r\n', '【中心专家】本中心现有从业人员15人，其中：医生6人，工程技术人员3人，护理人员4人，其他管理和医疗辅助人员2人；医技人员中，高级职称者3人，中级职称者3人。本中心聘请我国著名放射学专家、华山医院原院长陈星荣教授，上海医学放射学会秘书长、仁济医院放射科主任许建荣教授等为顾问、会诊专家定期或不定期地在本中心进行疑难病例讨论、会诊。', 'admin', 12, 0, 5, 2, '31.20726,121.524152', '上海市浦东新区东方路1630号仁济医院东部2号楼', 517),
(34, '上海455医院PET-CT高端体检中心', '上海', '7500', '部队三甲', '2009-08-10 20:07:32', '114.81.19.25', '【中心介绍】全国首家以PET-CT高级检查和常规体检一体化管理的高端体检中心。中心总面积1200多平方米，拥有目前最先进的数字化体检管理平台、能够保证体检结果的快速（3个工作日）、准确、可靠，同时实现了Internet网上查询及短信查询功能。\r\n\r\n【设备型号】Discovery ST16 PET－CT\r\n\r\n\r\n\r\n【乘车路线】轨道交通：地铁三号线、四号线虹桥路下 公交：48、72、113、26、76、572、806、138、506路均可 火车：上海火车站西南出口乘113路公交车到番禺路站下 飞机：虹桥机场乘坐806路公交车至番禺路站下', '【中心专家】杨春山：上海455医院PET－CT中心医疗主任，第二军医大学附属长征医院影像专业博士、复旦大学附属中山医院影像专业博士后、副主任医师。从事CT、MRI诊断20余年，擅长胸、腹部肿瘤的早期诊断。以第一作者发表论文30余篇，获军队科技进步二、三、四等奖6项，获上海市博士后及上海市科委PET－CT重点基金各一项。\r\n \r\n    陈宪英：中心核医学专家，研究生导师。从事临床核医学专业30多年，主要研究方向：肿瘤核医学及核心脏病学，获省部级科学进步二等奖2项，军队科技进步三等奖1项，四等奖8项，在核心期刊发表论文50余篇。\r\n\r\n    马庆和：中心影像学专家，中华医学会甘肃分会超声影像学专业委员会常务理事，兰州大学医学院副教授，省级医学杂志常务编委，中石化总公司职称晋升委员会委员。从事临床及影像诊断工作近30年，对各种疾病的CT诊断及超声诊断有丰富的临床经验。曾担任甘肃及广州三级甲等医院影像科主任多年，撰写影像专业论文20余篇，编著《CT临床200问》一部。  ', 'admin', 10, 0, 5, 1, '31.234908,121.452881', '上海市淮海西路33号，靠番禹路', 399),
(39, '上海肿瘤医院PET-CT中心', '上海', '7500', '三甲', '2009-08-12 12:37:41', '114.81.25.17', '【医院介绍】是国家教育部直属的集医疗 教学 科研 预防为一体的三级甲等医院 医院创建于1931年 是我国成立最早的肿瘤专科医院\r\n\r\n【设备型号】 SMS biograph 16 ( Hi-Rez) \r\n\r\n【乘车路线】乘49、50、72、89、41、44、218、104、572路及隧道二线\r\n', '', 'admin', 0, 0, 5, 4, '31.199381,121.428195', '上海市东安路270号门诊大楼地下2楼', 252),
(37, '浙江宁波明州医院PET-CT中心', '浙江', '9000', '', '2009-08-11 10:10:40', '116.228.54.246', '【医院介绍】宁波明州医院凝波明州医院是由奥克斯集团投资建设的一所集医疗、科研、教学、康复、疗养、保健为一体的一所大型现代化综合性医院。\r\n\r\n【中心介绍】于宁波市鄞州区,位于泰安西路168号,毗邻南部商务区，PET-CT中心引进当今国际最先进的PET-CT系统\r\n\r\n【设备型号】GE Disvovery ST16。\r\n\r\n\r\n\r\n\r\n【乘车路线】火车南站和汽车南站：乘公交560、514、353、906、10、106 路至李惠利医院（或交警支队）站下，换乘102或360路至明州医院（终点站）。乘出租车约需20元。\r\n汽车北站：乘公交350 路至灵桥东站下，换乘102 路至明州医院（终点站）。\r\n鄞州汽车站：乘公交102、360 路至明州医院（终点站）。\r\n市客运中心：乘公交369 路至三星奥克斯站下，换乘102 路至明州医院（终点站）。\r\n汽车东站：乘公交516、15、556 路至七塔寺站下，换乘102 路至明州医院（终点站）。\r\n\r\n位置：医院北门内东侧核医学楼二楼\r\n\r\n\r\n', '【中心专家】 李光军主任  毕业于大连医科大学影像与核医学专业，硕士学位。曾工作于辽宁省肿瘤医院放射科、中山大学附属肿瘤医院PET/CT、上海交大附属仁济医院PET/CT，2007年-2008年在上海复旦大学附属肿瘤医院PET/CT主诊。先后在《中国公共卫生杂志》、《中国临床影像杂志》《中国实用内科杂志》《大连医科大学早报》《国外医学临床放射学分册》《中国临床医学影像学杂志》发表论文数篇。\r\n\r\n孙达，男，主任医师，中国医学影像技术研究会常务理事兼核医学分会副主任委员、浙江省核学会理事兼副秘书长、浙江省核医学会副主任委员。从事核医学工作已35年。曾在美国加州大学洛杉矶分校（UCLA）进修3年，重点学习和研究核素脑显像及PET的应用。编写和主编专著《放射性核素脑显像》和《放射性核素骨显像》，另外还参与主编、副主编和编写专著6本。在《J of Nucl Med (美国核医学杂志)》、《中华核医学杂志》、等国内外刊物和学术会议上发表论文60余篇。', 'admin', 22, 0, 5, 4, '29.825306,121.568012', '泰安西路168号', 228),
(38, '上海瑞金医院PET-CT中心', '上海', '7500', '三甲', '2009-08-12 12:32:53', '114.81.25.17', '', '', 'admin', 0, 0, 2, 10, '31.235114,121.464844', '', 206),
(40, '上海武警总队医院PET-CT中心', '上海', '7500', '部队三甲', '2009-08-12 12:38:08', '114.81.25.17', '【医院介绍】1982年8月由上海警备区警备师医院改编为 "武警上海市总队医院"。1990年11月医院新院正式落成使用，医院进入了全面发展时期，已成为集医疗、教学、科研、预防为一体的大型综合性“三级甲等”医院。\r\n\r\n【中心介绍】于2003年初成立了健康体检中心，在体检方式上从单一门诊体检逐渐发展为门诊体检、住院体检、上门体检等多种形式，在服务对象上从单一部队官兵发展为社会各界人士。\r\n\r\n\r\n【设备型号】GE DISCOVERY ST', '', 'admin', 0, 0, 5, 5, '31.285593,121.464844', '上海市长宁区虹许路831号', 160),
(41, '上海华山医院PET-CT中心', '上海', '7500', '三甲', '2009-08-12 12:38:26', '114.81.25.17', '【医院介绍】上海华山医院创建于1907年，1992年首批通过国家三级甲等医院评审，目前已成为国家级高层次的医教研中心之一，是复旦大学附属综合性教学医院和中国红十字会直属医院，是上海地区中国人最早创办的医院，在国内外享有较高声誉。\r\n\r\n【中心介绍】复旦大学附属华山医院PET中心于1996年由核医学科提出申请并做了技术方案论证，经院部批准报市卫生局、1997年3月国家卫生部批准立项。\r\n\r\n\r\n\r\n【设备型号】Siemens EXACT HR+型\r\n\r\n', '【中心专家】2005年4月许震生同志任PET中心主任，管一晖、赵军担任PET中心副主任，管一晖、赵军仍继续担任核医学科副主任。至2006年8月，科室人员13人，固定编制11人。', 'admin', 0, 0, 4, 3, '31.210991,121.430631', '上海市吴中东路518号', 239),
(42, '浙江嘉兴武警医院PET-CT中心', '浙江', '9000', '部队三甲', '2009-08-12 12:39:05', '114.81.25.17', '【医院介绍】浙江武警总队医院位于党的诞生地嘉兴南湖之畔　始建于1940年　1983年由解放军第111医院改编为武警浙江省总队医院　医院占地面积12．7万平方米　建筑面积9．6万平方米　其中医疗用房7．7万平方米。\r\n\r\n\r\n\r\n【设备型号】GE Discovery 16sT\r\n\r\n【乘车路线】交通：公交7路武警医院站下，汽车北站、汽车西站、雀幕桥等地设免费班车往返（上午7：30-11：30，下午12：45-15：45，每30分钟一班） \r\n', '【中心专家】余党凡 主任医师 ', 'admin', 0, 0, 5, 10, '35.86166,104.195397', '浙江嘉兴南湖区南湖路16号', 146),
(43, '浙江湖州98医院PET-CT中心', '浙江', '8990', '部队三甲', '2009-08-12 12:39:32', '114.81.25.17', '【医院介绍】解放军第98医院位于浙江湖州市，建院百年，是集医疗、教学、科研、预防、保健于一体的大型三级甲等综合性园林式医院。现医院斥巨资引进国内为数不多的德国西门子公司最新PET/CT，该设备功能齐全、图像清晰、技术领先。\r\n\r\n【中心介绍】解放军第98医院PET/CT中心，技术实力和服务质量在申苏浙皖等地独树一帜。由浙江大学第二医院原核医学科主任孙达教授担任中心主任，常年坐诊进行肿瘤的全面诊断。\r\n\r\n\r\n\r\n【乘车路线】乘4路、6路、9路、2路、1路至九八医院站下车。\r\n', '【中心专家】孙达教授担任中心主任，现任中国医学影像技术研究会常务理事兼核医学分会副主任委员、浙江省核学会理事兼副秘书长、浙江省核医学会副主任委员。', 'admin', 0, 0, 5, 6, '31.230708,121.472916', '湖州市车站路9号', 169),
(45, '浙江杭州117部队医院PET-CT中心', '浙江', '9000', '部队三甲', '2009-08-12 12:53:47', '114.81.25.17', '【医院介绍】中国人民解放军第117医院位于杭州西湖灵隐风景区，离西湖和灵隐均不足500米，周围被植物园环抱，院内环境优美，四季鸟语花香，素有花园式医院之称。\r\n\r\n【中心介绍】体检中心是医院按四星级酒店标准建造的集体检、住宿、餐饮、娱乐和康复于一体的综合性保健中心，占地面积近2万平方米，专用停车场可停车上百辆，拥有国际上最先进的医疗检查设备，还拥有舒适典雅的各类客房、套房及大小会议室，餐厅、棋牌、健身中心等。中心作为一所三级甲等医院的专业健康体检中心，具备雄厚的专业背景、专业技能、专业设施和军队医院的良好信誉，是一家最具现代化、专业化、规模化和目前华东地区最高档的健康体检中心。', '', 'admin', 4, 0, 4, 11, '30.759539,104.110565', '杭州市西湖风景区灵隐路14号', 132),
(78, '江苏苏州九龙医院PET-CT中心', '江苏', '7500', '', '2009-10-11 09:07:27', '116.228.54.246', '【医院介绍】由香港九龙集团投资兴建，并与上海交通大学医学院合作建设的一所大型中外合资、合作医院，是苏州工业园区政府的公共设施重点配套工程之一。\r\n\r\n\r\n\r\n【设备型号】西门子Biograph 2 PET/CT\r\n\r\n【乘车路线】2路、6路、28路、47路、100路、219路、812路。\r\n\r\n', '【中心专家】刘建军 核医学专家，硕士研究生学历，上海交通大学医学院苏州九龙医院核医学科科主任，副主任医师。上海交通大学医学院临床核医学研究所办公室主任，上海市核医学会中青年委员。', 'llz007', 3, 0, 4, 0, '31.597179,120.288084', '苏州工业园区万盛街118号', 133),
(50, '江苏江阴市华西村医院PET-CT中心', '江苏', '7500', '', '2009-08-12 13:02:39', '114.81.25.17', '【中心介绍】 江阴华西健康体检中心是华东地区第一家设备最先进、功能最齐全的专业健康体检机构。本中心以国际健康体检标准为依据、与解放军第二军医大学上海长海医院共同创建，并按照星级酒店标准建立了集体检、疗养、康复、旅游、住宿、餐饮、娱乐于一体的综合性保健中心。目前，体检中心已拥有当今世界最先进的德国西门子PET/CT，美国GE16层螺旋CT、DR、CR，以及乳腺红外线、钼靶、IU22、HD11彩超、胃镜、肠镜、肺功能、经颅多普勒、骨密度、动脉硬化和人体成分分析等众多国际先进的医疗设备。\r\n\r\n\r\n\r\n【设备型号】西门子公司Biograph 39环大视野LSO晶体PET带16层螺旋CT的PET/CT\r\n\r\n【乘车路线】\r\n', '【中心专家】王凌元 主任医师、教授。第二军医大学长海医院主任医师、教授，专业技术5级，大校军衔。历任二军大外训系参谋，长海医院主任医师、教授。现任长海医院华西诊疗中心主任。', 'admin', 0, 0, 4, 20, '31.861793,120.542093', '江阴市华西村幸福园6号塔', 186),
(51, '苏州无锡九龙医院PET-CT中心', '无锡', '8800', '三甲', '2009-08-12 13:04:12', '114.81.25.17', '', '', 'admin', 0, 0, 0, 0, '', '', 18),
(52, '成都军区总医院PET-CT中心', '四川', '9800', '部队三甲', '2009-08-12 13:05:01', '114.81.25.17', '【中心介绍】中心技术力量雄厚。现有主要工作人员12人，其中高级职称3人，专业水平高，有力的保证了报告的及时性和准确性。2008年7月，中心与西门子（中国）有限公司合作，成立了西南分子影像技术合作中心。\r\n\r\n【中心专家】周克，副主任医师，中心主任。1984年7月毕业于第三军医大学，2005年任成都军区总医院PET中心主任。擅长肿瘤疾病的诊治、放射介入及CT、磁共振、PET等影像学诊断。\r\n\r\n【设备型号】德国西门子Biograph Sensation16型PET/CT\r\n\r\n', '', 'admin', 1, 0, 0, 20, '31.345721,120.656662', '成都市外北天回镇', 86),
(59, '北京第二炮兵总医院PET-CT中心', '北京', '9800', '部队三甲', '2009-08-12 13:25:28', '114.81.25.17', '【医院介绍】第二炮兵总医院是一所集医疗、教学、科研、预防保健为一体的综合性三级甲等医院。PET－CT高端体检中心是本院的重点学科。医院投入巨资，引进当今世界最新型美国GE公司的PET－CT（Discovery STE)，建立了以影像医学和核医学著名专家一体化管理的高端体检中心。\r\n\r\n【中心介绍】PET-CT高端诊断中心位于二炮总医院住院部部南边，北京师范大学京师大厦斜对面，占地 1800 多平方米，配备有世界一流的PET-CT机、医用回旋加速器及相应在的配套设备，中心定编医务人员 10 人，拥有多名具有影像诊断和核医学诊断经验的专业医务人员，并且聘请有国内一流的顶尖知名专家为顾问。设有专门的导诊护士全程陪同检查。中心与国内知名的解放军第四五五医院、华山医院建立良好学术交流平台，技术与世界同步。\r\n\r\n【中心特色】为帮助医务人员和受检者解决临床困惑，正确解读PET－CT片，中心汇集北京著名影像专家、核医学专家和临床各科室专家、定期免费为广大受检者提供医疗咨询服务。PET－CT大会诊时间：每周二、周四下午3－5点.\r\n\r\n\r\n\r\n', '【专家介绍】\r\n    李德鹏  副主任医师 医学影像与核医学专业博士，中心医疗主任，中华医学会核医学分会第七届委员会科普专家，原首都医科大学宣武医院PET中心业务骨干和诊断组组长，师从我国影像学泰斗李坤成教授，研究方向为PET显像与CT、MRI的结构变化，从事PET的临床诊断与应用研究工作十余年，擅长将PET的功能代谢影像与病灶的CT、MRI结构影像有机结合，根据病灶的代谢特征和结构变化特征，做出恰当的、符合临床要求的诊断报告。\r\n    黄志强  中心主任，医学博士，毕业于中南大学湘雅医学院。曾先后在湖南湘雅医院、上海411医院伽玛刀中心、江西省人民医院伽玛刀中心、上海仁济医院PET-CT中心等担任管理工作。从医十余年，具有丰富的肿瘤早期诊治经验和一定的经营管理阅历。参与了早期体部伽玛刀的基础和临床研究，建立了影像与核医学一体化管理的PET-CT会诊中心，创立了全国首家PET-CT和常规体检一体化管理的高端体检中心。\r\n    ', 'admin', 4, 0, 5, 34, '39.930089,116.389924', '北京市西城区新外大街16号', 208),
(61, '广州医学院第一附属医院PET/CT中心', '广东', '9000', '三甲', '2009-08-12 13:26:03', '114.81.25.17', '', '', 'admin', 0, 0, 3, 0, '', '', 87),
